SnippetCode.val:
using System;
using System.Net.Mail;

public static class Snippet
{
	public static System.String SendMail(System.String sServer, System.Int32 wPort, System.String sTo, System.String sFrom, ref System.String sSubject, ref System.String sBody)
	{
		String sRetval = "";

		try {
			MailMessage message = new MailMessage(sFrom, sTo);
			message.Subject = sSubject;
			message.Body = sBody;

			SmtpClient client = new SmtpClient(sServer, wPort);

			// Credentials are necessary if the server requires the client 
			// to authenticate before it will send email on the client's behalf.
			client.UseDefaultCredentials = true;

			client.Send(message);
		}  
		catch (Exception ex) {
			sRetval = ex.ToString();			  
		}

		return sRetval;
	}
}
